mapper.select:
SELECT 
FROM tab_user_weixin_info


		AND subscribe = #{subscribe}


		AND openid = #{openid}


		AND nickname = #{nickname}


		AND sex = #{sex}


		AND language = #{language}


		AND city = #{city}


		AND province = #{province}


		AND country = #{country}


		AND headimgurl = #{headimgurl}


		AND subscribe_time = #{subscribeTime}


		AND wxserverid = #{wxserverid}


		AND createtime = #{createtime}

SELECT 
FROM tab_user_weixin_info


		AND subscribe = #{searchFields.subscribe}


		AND openid = #{searchFields.openid}


		AND nickname = #{searchFields.nickname}


		AND sex = #{searchFields.sex}


		AND language = #{searchFields.language}


		AND city = #{searchFields.city}


		AND province = #{searchFields.province}


		AND country = #{searchFields.country}


		AND headimgurl = #{searchFields.headimgurl}


		AND subscribe_time = #{searchFields.subscribeTime}


		AND wxserverid = #{searchFields.wxserverid}


		AND createtime = #{searchFields.createtime}

mapper.update:
UPDATE tab_user_weixin_info
subscribe = #{subscribe}, openid = #{openid}, nickname = #{nickname}, sex = #{sex}, language = #{language}, city = #{city}, province = #{province}, country = #{country}, headimgurl = #{headimgurl}, subscribe_time = #{subscribeTime}, wxserverid=#{wxserverid}, createtime=#{createtime}
WHERE id = #{id}
